void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //num de frames en la img
    float numFrames = 6.0;

    //Elegir frame by frame
    float currentFrame = floor(mod(iTime * 10.0, numFrames));

    vec2 uv = fragCoord.xy/iResolution.xy;
    uv *= 3.0;
    uv = fract(uv);

    //Anim      
    float frameWidth = 40.;
    uv.x = (uv.x + currentFrame)/numFrames * (frameWidth*numFrames)/iChannelResolution[0].x; 

    // ------------------------------------------------
    //Anim - my invented way
    //Calcular el tamaño de cada frame
    //float frameWidth = iChannelResolution[0].x/numFrames;

    //Como los frames no están posicionados con exactitud le pongo un offset
    //float frameOffset = 2.66667;

    //uv.x = (fragCoord.x)/iResolution.x/numFrames +
    //       (frameWidth - frameOffset)/iChannelResolution[0].x * currentFrame -0.005;
    // ------------------------------------------------

    vec4 text = texture(iChannel0, vec2(uv.x,uv.y));

    if(text == vec4(1.0,0.6,1.0,1.0))
    {
        float blue = (sin(iTime * 2.0) +1.0)/2.0;
        fragColor = vec4(0.5,uv.y,blue,1.0);
    }
    else
    {
        fragColor = text;
    }
}
