float n21(vec2 vec) {
    return fract(sin(dot(vec, vec2(167.3,235.6)))*53243.176554);
}
vec2 unit(float angle) {
    return vec2(cos(angle), sin(angle));
}
mat2 rotate(float angle) {
    return mat2(unit(angle), unit(angle + 3.14159 * 0.5));
}
float gridNoise(vec2 vec) {
    vec2 frac = fract(vec);
    vec2 id = floor(vec);

    frac = frac*frac*(3.0-2.0*frac);

    float tl = n21(id + vec2(0.0, 0.0));
    float bl = n21(id + vec2(0.0, 1.0));
    float tr = n21(id + vec2(1.0, 0.0));
    float br = n21(id + vec2(1.0, 1.0));

    float t = mix(tl, tr, frac.x);
    float b = mix(bl, br, frac.x);

    float c = mix(t, b, frac.y);

    return c;
}
float smoothNoise(vec2 vec) {
    float c = gridNoise(vec);
    c += gridNoise(vec * rotate(0.2) * 2.0) * 0.5;
    c += gridNoise(vec * rotate(0.4) * 4.0) * 0.25;
    c += gridNoise(vec * rotate(0.6) * 8.0) * 0.125;
    c /= 1.875;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    vec3 scheme = vec3(0.8, 0.2, 0.3) * (0.85 + sin(iTime) * 0.15);

    float d = length(uv) - 0.5 - cos(iTime) * 0.025;
    float circleDist = abs(d) - 0.05;

    if (circleDist < 0.0) {
        col = abs(circleDist * 25.0) * scheme;
    }
    if (d+0.04 < 0.0) {
        col = (1.0 + d * 2.0) * smoothNoise(uv * 10.0 + vec2(0.0, iTime)) * 2.0 * scheme;
    }



    fragColor = vec4(col, 1.0);
}
