vec3 palette( float t ){
     vec3 a = vec3(1.0, 0.8, 0.9);  
     vec3 b = vec3(0.9, 0.4, 0.6);  
     vec3 c = vec3(0.0, 0.0, 0.0);  
     vec3 d = vec3(0.0, 0.0, 0.0);  
     return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    vec3 finalcol = vec3(0.);
    uv.x *= iResolution.x / iResolution.y;
    vec2 uv0 = uv;
    for (float i = 0.0; i < length(uv0) * 3.; i++) {
        uv = fract(uv) * 2.0 - 1.0;

        float d = sin(length(uv)) / exp(length(uv0));

        d = sin(d * 9. + iTime) / 0.7;
        d = abs(d);
        d = pow(0.02 / d, 0.6);
        vec3 col = vec3(d, d, d);
        col = palette(length(uv0) + (iTime) * 0.1);

        finalcol += col * d;
    }
    fragColor = vec4(finalcol, 2.);
}
