// Hash function to generate pseudo-random but consistent positions
vec2 hash2( vec2 p ) {
    p = vec2( dot(p, vec2(127.1,191.7)), dot(p, vec2(269.5,183.3)) );
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}


float voronoiFragment(vec2 uv, float cellCount, float minDist){
    vec2 grid = floor(uv * cellCount);
    for (int x = -1; x <= 1; x++) {
        for (int y = -1; y <= 1; y++) {
            // Neighbor cell ID
            vec2 cell = grid + vec2(float(x), float(y));
            // Get pseudo-random position for the cell
            vec2 cellPos = hash2(cell) * 0.5 + 0.5;
            // Cell position in normalized space
            vec2 pos = cell / cellCount + cellPos / cellCount;
            // Compute distance to the cell's point
            float dist = length(uv - pos);
            // Update minimum distance if necessary
            minDist = min(minDist, dist);
        }
    }
    return minDist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.y;// * 2.0 - 1.0;
    float minDist = 1.0; // Start with a large number outside of expected range

    // Determine the grid cell the fragment is in
    float cellCount = 3.0;
    minDist = voronoiFragment(uv, cellCount, minDist);

    // Use the distance for coloring: closer to cell center is darker
    fragColor = vec4(vec3(1.0 - minDist * cellCount), 1.0);
}
