#define maxiter 50

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0 * (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    int iter = 0;

    vec2 c = vec2(1.0);
    c = 0.7885 * c;
    c.x *= sin(iTime);
    c.y *= cos(iTime);

    float x = uv.x, y = uv.y, xtemp = 0.0;
    while((x * x + y * y )<= 4.0 && iter < maxiter){
        xtemp = x * x - y * y + c.x;
        y = 2.0 * x * y + c.y;
        x = xtemp;
        iter++;
    }

    vec3 col = float(iter)/float(maxiter)*vec3(1, 1, 1);

    fragColor = vec4(col,1.0);
}
