float fn1(
    vec2 o, 
    float n_t2
){
    float n_tau = radians(360.);
    float n = length(o);
    float n_t = 0.2*iTime;//+n_t2;
    float n_ang_nor = fract(atan(o.y, o.x)/n_tau);

    float n2 = sin(
        n*3.*n_tau
        * sin(
            n*2.+n_t+n_t2*2.
            + sin(n_ang_nor*n_tau*3.)
        )
    )*.5+.5;
    return n2;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float n_min = min(iResolution.x, iResolution.y);
    vec2 o = (fragCoord.xy-iResolution.xy*.5)/n_min;
    float n_tau = radians(360.);
    float n = length(o);
    float n_t = 0.2*iTime;
    float n_ang_nor = fract(atan(o.x, o.y)/n_tau);
    float no = 0.02;
    vec3 oc = vec3(
        fn1(o,n_t+1.*no),
        fn1(o,n_t+2.*no),
        fn1(o,n_t+3.*no)
    );
    vec3 oc2 = vec3(
        fn1(o,n_t*2.*no),
        fn1(o,n_t*3.*no),
        fn1(o,n_t*4.*no)
    );
    float nl = sin(iTime)*.5+.5;
    oc = nl*oc+ (1.-nl)*oc2;

    fragColor = vec4(oc.xyz, 1.);
}
