#define R iResolution
#define m cos(iTime / 7.)*.5
float w = .06, h = .04, d = .071;

float rct(vec2 i){
    float r = max(abs(i.x/w), abs(i.y/h)) - .5 ;
    return smoothstep(0.,7.7/R.y,r);
}


void mainImage( out vec4 o, vec2 i )
{
 vec2 uv = i/R.xy-.5;
 vec3 col = (iMouse.z<1.) ? vec3(step(fract(i.x/R.x*30.),.5)):vec3(.6);
 o = vec4(mix(vec3(0,.05,.5),mix(vec3(1,1,0), col, rct(uv-vec2(m,d))),rct(uv-vec2(m,-d))),1.) ;
}
