float N21(vec2 p)
{
    p += vec2(9.4,1.2); // evitamos el 0
    p = fract(p*vec2(138.38,812.67));
    p += dot(p,p+23.54);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n, N21(p+n));
}


float HexDist(vec2 uv)
{
    uv = abs(uv);
    float c = dot(uv, normalize(vec2(1.,1.73)));
    return max (c, uv.x);
}

vec4 HexUV(vec2 uv)
{

    vec2 r = vec2(1,1.73);
    vec2 hr = r*.5;

    vec2 a = mod(uv,r)-hr;
    vec2 b = mod(uv-hr,r)-hr;

    vec2 gv;

    if(length(a)<length(b))
        gv = a;
    else
        gv = b;

    //gv.x = abs(gv.y/gv.x);
    //gv.y = abs(gv.x/gv.y);
    float x = atan(gv.x,gv.y);
    float y = .5-HexDist(gv);
    vec2 id = uv-gv;
    return vec4(x,y,id.x,id.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float h = HexDist(uv);
    h = step(h,.4);

    vec3 col = vec3(0.);
    //col = vec3(h);
    col.z = 0.;

    uv*=10.;

    uv.x+=iTime;
    uv.y+=2.;
    //uv.y=abs(uv.y);
    vec4 hc = HexUV(uv+100.);
    //float n= N21(hc.zw);

    float c = hc.y;
    float r = 0.01;
    float m = sin(hc.z*hc.w+iTime);
    m*=m*m*m*m*m;
    float m2= sin((hc.z+2.)*(hc.w+2.)+iTime);
    m2*=m2*m2;
    float m3= sin((hc.z+2.5)*(hc.w+2.5)+iTime);
    m3*=m3*m3;
    c = smoothstep(r,r+.005,c*m);
    //col.rg=hc.zw;
    col = c*vec3(0.5,1.3,.7);
    //col.b=0.;
    c = max(c, smoothstep(r,r+.005,c*m2));
    //col.rg=hc.zw;
    col += c*vec3(1.5,0.3,.9);
    //c = smoothstep(r,r+.005,c*m3);
    //col.rg=hc.zw;
    //col *= c*vec3(0.5,2.3,.4);

    /*
    if(n<.02)
    {
        col*=1.-vec3(hc.x,hc.y,0.);
    }
    */
    fragColor = vec4(col,1.0);
}
