void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texelFetch(iChannel0,ivec2(fragCoord),0);

    fragColor = vec4(col); // Output to screen
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5 + vec2(iResolution.x * sin(iTime) * 0.3,0.);
	float radius = 0.05 * iResolution.y;

	// Circle
	vec3 white = vec3(1.);
	vec4 circle = drawCircle(uv, center, radius, white);

	// Blend the two
	fragColor = circle - vec4(vec3(0.01),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 prev = texture(iChannel1, uv).rgb;
    vec4 acol = texture(iChannel0, uv);
    fragColor = vec4(clamp(acol.rgb + prev, 0.,1.0), 1.0);
}

vec4 drawCircle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(1.0 - t);
}
