vec3 hash(vec3  p) { 
    p = vec3(dot(p,vec3(127.1,311.7, 253.4)), 
             dot(p,vec3(269.5,183.3, 214.2)), 
             dot(p,vec3(138.5,242.5, 162.8))); 
    return fract(sin(p)*55236.5253); 
}

vec3 ACESFilm(vec3 x) {
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e), 0.0, 1.0);
}

float voronoi(in vec3 coord) {
    vec3 icoord = floor(coord);
    vec3 fcoord = fract(coord);
    float result = 9999.0;
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            for (int k = -1; k <= 1; k++) {
                vec3 local = vec3(float(i), float(j), float(k));
                vec3 rand = hash(icoord + local);		
                float dist = length(local - fcoord + rand);
                result = min(result, dist);
            }
        }
    }
	return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2  p = fragCoord / iResolution.y;
    float v = voronoi(vec3(6.0 * p, iTime));
    v = v * v + 0.05;
    vec3 col = vec3(v, v, v);
    fragColor = vec4(ACESFilm(col), 1.0);
}
