void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = 0.2;
    vec2 normCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    vec2 uv0 = fragCoord / iResolution.xy * 2.0 - 1.0;
    float d = length (uv0);

    angle = iTime*0.1;
    for (float i = 0.0; i < 32.0; i+=1.0){
        normCoord = abs(normCoord);
        normCoord = (normCoord-0.5) * 1.1;
        normCoord *=mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
            );
    }
    float green = length(normCoord)* cos(2.0*iTime) + d* 0.4;
    float blue =length(normCoord)* sin(iTime) + d*0.7;
    fragColor = vec4 (length(normCoord), green, blue, 1.0);
}
