void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 resolution = vec2(1920.0, 1080.0);
    float time = iTime;
    vec2 playerPos = vec2(0.0);
    vec2 uv = fragCoord.xy / resolution.xy;
    uv.y = 1.0 - uv.y;
    float uv_x = (uv.x * 2.0 - 1.0);
    float uv_y = (uv.y * 2.0 - 1.0);
    float r = -0.5 + 0.5 * cos(-time - time + uv_x * 200.0 * 1e-03 * 5.0 + -uv_y * 2.0 * 10.0 + 4.0);
    float g = 0.5 + -0.5 * cos(time - -time + uv_y * 200.0 * 1e-03 * 5.0 + -uv_x * 2.0 * 10.0 + 4.0) * 3.0;
    float b = -0.5 + 0.5 * cos(-time - time + -uv_y * 200.0 * 1e-03 * 5.0 + uv_x * 2.0 * 10.0 + 4.0);
    fragColor = vec4(-r, -b, -g, 1.0);
}
