const uint iterations = 8u;
void mainImage( out vec4 o, in vec2 p )
{
    vec2 uv = 16.*(2.*p-iResolution.xy)/iResolution.y;
    float val;
    for(uint i; i < iterations; i++)
    {
        val -= abs(uv.x);
        val *= uv.y;
        val = exp(cos(val));
    }
    val /= abs(uv.y);
    val = smoothstep(-.25,4.,val);
    o = vec4(val);
}
