void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int kernelSize = int(round(iMouse.x/iResolution.x * 30.));
    float divAmount = 1./pow(float(kernelSize)*2.+1.,2.);
    vec3 result;

    for(int i = kernelSize*-1; i <= kernelSize; i++)
    for(int j = kernelSize*-1; j <= kernelSize; j++)
    {
    result+= texture(iChannel0,vec2((fragCoord.x+float(i))/iResolution.x,(fragCoord.y+float(j))/iResolution.y)).rgb*divAmount;
    }
    result *= iMouse.y/iResolution.y * 2.;
    result -= 1.;

    if(result.r < 0.)
    result.r = 0.;
    if(result.g < 0.)
    result.g = 0.;
    if(result.b < 0.)
    result.b = 0.;

    fragColor = vec4(vec3(result),1.);
    fragColor += texture(iChannel0,vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y));
}
