float tunel(vec2 uv,float t){
   float a = (atan(-uv.y,uv.x))/(3.14159*2.0);
    float r = length(uv)+sin(iTime*t)*.5+0.5;
    float f= sin(length(abs(vec2(a,1./r))))*0.5+0.5;
    return f*r*0.75;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col =vec3(
    tunel(uv+vec2(0.1,.0),0.1),
    tunel(uv+vec2(0.1,.20),.5),
    tunel(uv+vec2(0.5,.0),-0.2)
    );

    fragColor = vec4(col*0.75,1.0);
}
