float hash13(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define DIST_MAX 1000.0

#define MAX_RAY_STEPS 64

vec3 triangle_center(vec3 a, vec3 b, vec3 c)
{
    return (a+b+c)/3.0;
}

vec3 triangle_normal(vec3 a, vec3 b, vec3 c)
{
    return normalize(cross(a - b, c - b));
}

float plane_intersection(vec3 pp, vec3 pn, vec3 ro, vec3 rd)
{
    return dot(pp - ro,pn)/dot(rd,pn);
}

float heightmap(vec2 p)
{
    float t = 1.0;

    return
        4.0
        *
        // edgy
        (
            1.0
            -
            mix
            (
                abs(sin(tau*(p.x+t)/20.0))*abs(cos(tau*(p.y+t)/20.0)),
                abs(sin(tau*((p.x+p.y)/sqrt(2.0)+t)/20.0))*abs(cos(tau*((-p.y+p.x)/sqrt(2.0)+t)/20.0)),
                0.5
            )
        )
        -
        1.0;

    return
        4.0
        *
        // smooth
        mix
        (
            0.5+0.5*sin(tau*(p.x+t)/20.0)*cos(tau*(p.y+t)/20.0),
            0.5+0.5*sin(tau*((p.x+p.y)/sqrt(2.0)+t)/20.0)*cos(tau*((-p.y+p.x)/sqrt(2.0)+t)/20.0),
            0.5
        );
}

// Construction of the radius formula:
// The leafs of the flower-shape are circle-arcs
// inside the unit-circle originating at (0,-1).
// Each circle-arc is tangential to the y axis.
// wxmaxima:
// circle:(x-x_0)^2+(y-y_0)^2=r^2; /* the leafs of the flower are circles */
// origin:(0-x_0)^2+(-1-y_0)^2=r^2; /* the origin is at (0,-1) */
// tangent:x_0=r; /* circles are tangent to y axis (orthogonal to unit circle) */
// solve([circle, origin, tangent],[x_0,y_0,r]);
// [[x_0=(y^2+2*y+x^2+1)/(2*x),y_0=-1,r=(y^2+2*y+x^2+1)/(2*x)]]
float ortho_circle_flower_sdf(vec2 p) // https://www.shadertoy.com/view/clGBzm orthogonal circles flower sdf 2, 2023 jt
{
    float n = 11.0; // must be odd integer

    float slice = pi/n;

    p.x = abs(p.x); // mirror horizontally
    float r = (p.y*p.y+2.0*p.y+p.x*p.x+1.0)/(2.0*p.x); // calculate radius of circular arc leaf passing through this point

    // equidistant circle segment endpoints on unit circle
    float r0 = tan(floor(atan(r)/slice)*slice); // to angle then quantize then to length
    float r1 = tan( ceil(atan(r)/slice)*slice); // to angle then quantize then to length

    // circle center
    vec2 c0 = vec2(r0,-1); // lower leaf
    vec2 c1 = vec2(r1,-1); // upper leaf

    // endpoints
    float mu = atan(p.x,p.y);
    mu = (floor(mu/slice/2.0)+0.5)*slice*2.0;
    vec2 c = vec2(sin(mu),cos(mu));

    return
        //length(p) < 1.0; // approximate
        dot(p, c) < 1.0 // thanks to tdhooper for fixing remaining discontinuities by changing length(p) < 1.0 to dot(p, c) < 1.0 (turns circle into polygon)
        ?
        min(abs(length(p-c0)-r0),abs(length(p-c1)-r1)) // circular arc leafs
        :
        length(p-c); // leaf endpoints
}

struct result
{
    vec3 normal;
    float dist;
    vec3 color;
};

result combine(result a, result b)
{
    //return a.dist < b.dist ? a : b; // ??? why doesn't this compile ???
    if(a.dist < b.dist)
        return a;
    return b;
}

vec2 sphere(vec3 ro, vec3 rd, vec3 ce, float ra) // modified https://iquilezles.org/articles/intersectors/
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    vec3 qc = oc - b*rd;
    float h = ra*ra - dot(qc, qc);
    if(h < 0.0) return vec2(DIST_MAX); // no intersection
    h = sqrt(h);
    vec2 d = vec2(-b-h, -b+h);
    return vec2(d.x < 0.0 ? DIST_MAX : d.x, d.y < 0.0 ? DIST_MAX : d.y);
}

vec2 ellipse(vec3 ro, vec3 rd, vec3 ra) // modified https://iquilezles.org/articles/intersectors/
{
    vec3 ocn = ro/ra;
    vec3 rdn = rd/ra;
    float a = dot( rdn, rdn );
    float b = dot( ocn, rdn );
    float c = dot( ocn, ocn );
    float h = b*b - a*(c-1.0);
    if(h < 0.0) return vec2(DIST_MAX); //no intersection
    h = sqrt(h);
    vec2 d = vec2(-b-h,-b+h)/a;
    return vec2(d.x < 0.0 ? DIST_MAX : d.x, d.y < 0.0 ? DIST_MAX : d.y);
}

#define ESSENTIAL_MATRIX

vec3 improved_texture_lookup(vec3 ray_pos_orig, vec3 ray_dir_orig, mat2x3 transform, vec3 center, vec3 normal, vec2 coords) // thanks to pyblob, joins triangles more smoothly
{
    // bypass fract, still has problems at occlusions
    vec3 color = textureGrad(iChannel0, fract(coords/5.0), dFdx(coords/5.0), dFdy(coords/5.0)).xyz;
/*
    // analytic gradients (assuming camera is not at infinity) ... let's hope, I got the derivatives correct. Though, I think you can still simplify a bit.
    // mat3x2(-1,0, 1,0, 0,1) * flip * flip * T * (rd.xy * dot(pp - ro, pn) / dot(rd, pn));
    vec3 rd = ray_dir_orig, ro = ray_pos_orig;//, pp = m, pn = normal;
    vec2 px = (dFdx(rd.xy) - dot(dFdx(rd), normal) / dot(rd, normal) / 2. * rd.xy) * dot(center - ro, normal) / dot(rd, normal);
    vec2 py = (dFdy(rd.xy) - dot(dFdy(rd), normal) / dot(rd, normal) / 2. * rd.xy) * dot(center - ro, normal) / dot(rd, normal);
    mat2 Q = mat3x2(-1,0, 1,0, 0,1) * transform;
    vec3 color = textureGrad(iChannel0, fract(coords/5.0), Q*px/5.0, Q*py/5.0).xyz;
*/
    return color;
}
#ifdef ESSENTIAL_MATRIX
mat2x3 triangle_matrix()
{
    return
        mat2x3 // using normalize is way easier to remember than all the square-roots
        (
            normalize(vec3(+1,-1, 0)), // NOTE: sum of components is zero
            normalize(vec3(-1,-1, 2))  // NOTE: sum of components is zero
        );
}
#else
mat3 triangle_matrix() // easier to remember than all the square-roots
{
    vec3 a = vec3(+1,-1,0);
    vec3 c = vec3(-1,-1,-1);
    vec3 b = cross(c, a);
    return mat3(normalize(a),normalize(b), normalize(c));
}
#endif
result triangle_trace(vec3 ray_pos_orig, vec3 ray_dir_orig) // step triangles by stepping through voxels along plane orthogonal to main diagonal (jt)
{
    result content = result(-ray_dir_orig, DIST_MAX, vec3(0)); // sky sphere
#ifdef ESSENTIAL_MATRIX
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat2x3 T = triangle_matrix();
    // step through voxels along plane orthogonal to main diagonal (1,1,1)
    vec3 ray_pos = T * ray_pos_orig.xy;
    vec3 ray_dir = T * ray_dir_orig.xy;
#else
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat3 T = triangle_matrix();
    // step through voxels along plane orthogonal to main diagonal (1,1,1)
    vec3 ray_pos = T * vec3(ray_pos_orig.xy, 0);
    vec3 ray_dir = T * vec3(ray_dir_orig.xy, 0);
#endif
    // initialization from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
    ivec3 tile = ivec3(floor(ray_pos));
    // decompose ray direction into reciprocal absolute ray direction and sign (-1,+1)
    vec3 delta_dist = 1.0 / abs(ray_dir); // reciprocal absolute ray direction
    ivec3 ray_step = ivec3(sign(ray_dir)); // sign of ray direction
    // initial distances to nearest walls by dimension:
    //     calculate ray position in relation to tile origin
    //     (requires adding one half because tile origin is floor of ray position)
    //     adjust sign according to ray direction
    //     because tile has size 1 and we start in the middle wall is located at 0.5 (sign depends on ray direction)
    //     divide by ray direction (to get distance with respect to ray)
    vec3 side_dist = (sign(ray_dir) * (vec3(tile) - ray_pos) + (sign(ray_dir) * 0.5) + 0.5) * delta_dist; // initial distances to nearest walls by dimension
    bvec3 nearest = bvec3(0); // component is true if nearest wall in that dimension
    for (int i = 0; i < MAX_RAY_STEPS; i++)
    {
        //bool even = abs(tile.x + tile.y + tile.z) == 2;
        bool even = ((tile.x ^ tile.y ^ tile.z) & 1) == 0;
        float flip = even?-1.0:+1.0;

        float tmin = length(vec3(nearest) * (side_dist - delta_dist)); // calculate distance to intersection point assuming ray_dir normalized

        // core from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
        bvec3 nearest_next = lessThanEqual(side_dist.xyz, min(side_dist.yzx, side_dist.zxy)); // component is true if nearest wall in that dimension
        vec3 side_dist_next = side_dist + vec3(nearest_next) * delta_dist; // update distances to nearest walls by dimension
        ivec3 tile_next = tile + ivec3(vec3(nearest_next)) * ray_step; // step to next tile in direction of nearest wall
        float tmax = length(vec3(nearest_next) * (side_dist_next - delta_dist)); // calculate distance to intersection point assuming ray_dir normalized

        {
            ivec3 tile_x = tile - (even ? ivec3(1,0,0) : ivec3(0,1,1));
            ivec3 tile_y = tile - (even ? ivec3(0,1,0) : ivec3(1,0,1));
            ivec3 tile_z = tile - (even ? ivec3(0,0,1) : ivec3(1,1,0));
#ifdef ESSENTIAL_MATRIX
            vec3 a = vec3(vec3(tile_x)*T,heightmap(vec3(tile_x)*T));
            vec3 b = vec3(vec3(tile_y)*T,heightmap(vec3(tile_y)*T));
            vec3 c = vec3(vec3(tile_z)*T,heightmap(vec3(tile_z)*T));
#else
            vec3 a = vec3(tile_x)*T + vec3(0,0,heightmap((vec3(tile_x)*T).xy)-sqrt(3.0));
            vec3 b = vec3(tile_y)*T + vec3(0,0,heightmap((vec3(tile_y)*T).xy)-sqrt(3.0));
            vec3 c = vec3(tile_z)*T + vec3(0,0,heightmap((vec3(tile_z)*T).xy)-sqrt(3.0));
#endif
            vec3 n = triangle_normal(a,b,c);
            vec3 m = triangle_center(a,b,c);

            float ground_dist = plane_intersection(m, n, ray_pos_orig, ray_dir_orig);

            if(ground_dist >= max(0.0, tmin) && ground_dist <= tmax)
            {
#ifdef ESSENTIAL_MATRIX
                vec3 local =
                    (
                        flip
                        *
                        T
                        *
                        (
                            ray_pos_orig.xy
                            +
                            ray_dir_orig.xy * ground_dist
                            -
                            vec3(tile)*T
                        )
                        +
                        1.0/3.0
                    );
#else
                mat3 Z = mat3(1,0,0,0,1,0,0,0,0);
/*
                vec3 local =
                    (
                        T*Z*(flip*(ray_pos_orig + ray_dir_orig * ground_dist - vec3(tile)*T))
                        +
                        1.0/3.0
                    );
*/
                mat3 M = mat3(+2,-1,-1,-1,+2,-1,-1,-1,+2)/3.0; // M=T*transpose(T) where last vector of T zeroed out - could T be calculated by factoring M?
                vec3 local = // NOTE: if last column of T zeroed then Z can be omitted here
                    (
                        flip
                        *
                        (
                            T*Z*ray_pos_orig
                            +
                            T*Z*ray_dir_orig * ground_dist
                            -
                            M*(vec3(tile))
                        )
                        +
                        1.0/3.0
                    );
#endif
                if(even) local = 1.0 - local;
                vec2 coords = vec2(local.y-local.x, local.z) + vec2(tile.y-tile.x, tile.z); // Calculate texture-coordinates from tile and local coordinates (trivial alternative below)
                //vec2 coords = sqrt(2.0)*vec2(-1.0,1.0/sqrt(3.0))*(ray_pos_orig + ray_dir_orig * ground_dist).xy; // D'oh - complicated texture coordinate computation above can be replaced by this simple line!

                vec3 lifted_ground_pos = ray_pos + ground_dist * ray_dir;
                //vec3 actual_ground_pos = ray_pos_orig + ground_dist * ray_dir_orig;
                vec3 vertex_id = hash33(lifted_ground_pos-fract(lifted_ground_pos-0.5)); // subdivides plane into hexagons and triangles

                //vec3 color = local; // debug barycentric coordinates
                //vec3 color = texture(iChannel0, fract(coords/5.0)).xyz; // simple lookup has artifacts at triangle boundaries
                vec3 color = improved_texture_lookup(ray_pos_orig, ray_dir_orig, mat2x3(T[0], T[1]), m, n, coords); // thanks to pyblob, joins triangles more smoothly
                //vec3 color = vec3(0.55,0.30,0.15)+0.1*(hash33((ray_pos_orig + ground_dist * ray_dir_orig)*123.0)*2.0-1.0);
                //vec3 color = fract(vec3(coords/10.0, 0.0)); // debug texture coordinates

                //color = hash33(vec3(tile)); // debug tile index
                //color = vertex_id; // debug vertex index

                content = combine(content, result(n, ground_dist, color)); // hit plane
            }

            if(tmin > 0.0) // XXX workaround because first step at 0 instead of at prism wall XXX
            {
                vec3 actual_prism_pos = ray_pos_orig + tmin * ray_dir_orig; // no need to tilt: distance matric normalized, so distance can be used with original origin and direction
                float h = 1.0;
                float d = dot(n, actual_prism_pos - m - vec3(0,0,h)); // orthogonal distance from prism wall to offset ground plane
                if(d < 0.0)
                {
                    vec3 v =
                        distance(actual_prism_pos.xy, a.xy) < distance(actual_prism_pos.xy, b.xy) && distance(actual_prism_pos, a) < distance(actual_prism_pos.xy, c.xy)
                        ?
                        a
                        :
                        distance(actual_prism_pos.xy, b.xy) < distance(actual_prism_pos.xy, c.xy)
                        ?
                        b
                        :
                        c;

                    vec3 lifted_prism_pos = ray_pos + tmin * ray_dir;
                    vec3 vertex_id = hash33(lifted_prism_pos-fract(lifted_prism_pos-0.5)); // subdivides plane into hexagons and triangles

                    //float x = fract(nearest.x ? lifted_prism_pos.y : nearest.y ? lifted_prism_pos.z : lifted_prism_pos.x);
                    //float x = fract(dot(lifted_prism_pos.yzx,vec3(nearest)))-0.5; // center of edge: looks flat
                    float x = fract(0.5+dot(lifted_prism_pos.yzx,vec3(nearest)))-0.5; // at vertex: 3d look by exploiting overlap
                    float y = 1.0+d/dot(n, vec3(0,0,h)); // local height above surface, (x,y) sheared
                    //float y = actual_prism_pos.z - v.z; // local height relative to nearest vertex: (x,y) discontinuous XXX glitches! why ?! XXX
                    //vec3 color = vec3(0,1,0);
                    //vec3 color = vec3(0,y,1.0-y);
                    //vec3 color = vec3(x,1.0-x,0);
                    float s = mix(0.2,0.5,hash13(vertex_id));
                    float flower = ortho_circle_flower_sdf(vec2(x,y)/s-vec2(0,1.0-0.2/*start slightly below surface*/))*s;
                    flower /= s*s*2.0; // tweak: make leafs of smaller plants QUADRATICALLY thinner
                    if(flower < 0.025)
                    {
                        //vec3 color = hash33(vec3(tile));
                        //vec3 color = hash33(vec3(tile))*vec3(0.5,1,0.1);
                        vec3 color = vertex_id*vec3(0.5,1,0.1);
                        //vec3 color = mix(vec3(0.25,1,0.1),vec3(0.75,1,0.1),hash33(vec3(tile)));
                        vec3 n = normalize(vec3((vec3(nearest) * T).xy, 0));
                        content = combine(content, result(n, tmin, color)); // hit plane
                    }


                }
            }
#ifdef PEBBLES
            {
                vec3 center = m;
                center.z -= 0.1;
                float r = hash13(m);
                float radius = mix(0.1, 0.5, r);
                vec2 dist = sphere(ray_pos_orig, ray_dir_orig, center, radius);
                if(dist.x < DIST_MAX)
                {
                    vec3 pos = ray_pos_orig + dist.x * ray_dir_orig;
                    vec3 n = normalize(pos - center);
                    content = combine(content, result(n, dist.x, vec3(r)));
                }
            }
#endif
#ifdef RAIN
            {
                float r = hash13(m);
                float t = fract(iTime/1.0+r);
                vec3 center = m;
                center.z += (1.0 - t) * 10.0;
                float radius = 0.0025*(0.5-0.5*cos(t*tau));
                //vec2 dist = sphere(ray_pos_orig, ray_dir_orig, center, radius);
                vec2 dist = ellipse(ray_pos_orig - center, ray_dir_orig, radius*vec3(1,1,50));
                if(dist.x < DIST_MAX)
                {
                    vec3 pos = ray_pos_orig + dist.x * ray_dir_orig;
                    vec3 n = normalize(pos - center);
                    content = combine(content, result(n, dist.x, vec3(1)));
                }
            }
#endif
        }

        if(content.dist < tmax)
            return content;

        // update state
        nearest = nearest_next;
        tile = tile_next;
        side_dist = side_dist_next;
    }

    return content;
}

void mainImage( out vec4 fragColor, in vec2 I )
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.25 * pi/2.0; // circle flight demo
    //pitch = !demo ? pitch : pi/2.0; // hover camera demo

    vec3 ray_pos = vec3(0.0, 0.0, -10.0);
    vec3 ray_dir = vec3(I.x, I.y, 2.0);
    ray_dir = normalize(ray_dir);

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ray_pos = M * ray_pos;
    ray_dir = M * ray_dir;
    ray_pos.z = max(ray_pos.z, heightmap(ray_pos.xy)+1.0); // hover camera above terrain

    result res = triangle_trace(ray_pos, ray_dir);
    vec3 color = res.color;
#ifdef WATER
    float water_level = 1.0;
    //if(-(ray_pos.z-water_level)/(ray_dir.z) < res.dist) color = vec3(0.0,0.1,1); // simple water
    float water_dist = -(ray_pos.z-water_level)/(ray_dir.z);
    if(water_dist > 0.0 && water_dist < res.dist)
    {
        vec3 dst = ray_pos + ray_dir * water_dist;
#ifdef ESSENTIAL_MATRIX
        mat2x3 T = triangle_matrix();
        vec3 center = vec3(fract(T * dst.xy - 0.5) * T, 0);
#else
        mat3 T = triangle_matrix();
        vec3 center = fract(T * vec3(dst.xy, 0) - 0.5) * T;
#endif
        float d = length(center.xy) * 2.0;
#ifdef RAIN
        // approximate ripples
        vec3 water_normal = normalize(vec3(normalize(center.xy) * cos(tau*(d*5.0-iTime))*0.2*exp(-d),1));
#else
        vec3 water_normal = vec3(0,0,1);
#endif
        ray_dir = reflect(ray_dir, water_normal); // reflect direction
        ray_pos = dst - ray_dir * water_dist; // reflect position
        res = triangle_trace(dst, ray_dir); // continue reflected ray from water surface
        res.dist += water_dist; // add distance from viewer to surface
    }
#endif

#ifdef RAIN
    vec3 skycolor = mix(vec3(0.2,0.4,0.8),vec3(0.2,0.3,0.5),abs(ray_dir.z));
#else
    vec3 skycolor = mix(vec3(0.2,0.4,0.8),vec3(0,0,1),abs(ray_dir.z));
#endif
    if(res.dist < DIST_MAX)
    {
        res.normal *= dot(res.normal, ray_dir) < 0.0 ? +1.0 : -1.0; // repair front-back

        vec3 dst = ray_pos + ray_dir * res.dist;
        vec3 light_dir = vec3(0);
        float light = 1.0;
        if(false) // directional light
        {
            light_dir = normalize(vec3(1,2,2));
#ifdef USE_SHADOWS
            light = triangle_trace(dst+res.normal*0.001, light_dir).dist < DIST_MAX ? 0.0 : 2.0;
#endif
        }
        else // point light
        {
            //vec3 light_pos = vec3(0,0,5);
            vec3 light_pos = vec3(10.0*vec2(cos(iTime/5.0),sin(iTime/5.0)),5);
            vec3 light_diff = light_pos - dst;
            float light_dist = length(light_diff);
            light_dir = light_diff / light_dist;
#ifdef USE_SHADOWS
            //shadow = triangle_trace(dst+res.normal*0.001, light_dir).dist < DIST_MAX ? 0.0 : 100.0 / (light_dist * light_dist);
            light = triangle_trace(dst+light_dir*light_dist, -light_dir).dist < light_dist - .001 ? 0.0 : 100.0 / (light_dist * light_dist); // suggested by pyblob to avoid artifacts (missing shadows)
#endif
        }

        float brightness = light * max(0.0, dot(res.normal, light_dir));
        vec3 diffuse = vec3(brightness);
        //vec3 diffuse = shadow ? vec3(0) : light_color * abs(dot(res.normal, light_dir)) / (light_dist * light_dist); // translucent
        float ambient = 0.01;

        //color *= (0.5 + 0.5 * result.xyz); // visualize normals
        color *= (diffuse + ambient); // apply light
        //color = vec3(fract(result.w));
#ifdef RAIN
        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(res.normal, normalize(-ray_dir + light_dir))), 250.0);
            color += specular;
        }
#endif
        color = mix(skycolor, color, exp(-pow(distance(ray_pos.xy,dst.xy)/25.0, 2.0))); // distance fog (but not vertically)
    }
    else // sky
    {
        color = skycolor;
    }
    color = tanh(color); // roll-off / soft-clamp overly bright colors
    color = sqrt(color); // approximate gamma
    fragColor = vec4(color, 1.0);
}
