#define PI 3.14159265358979
#define TWO_PI 6.28318530718

float periodicNoise(float x, float period, float amplitude) {

    return amplitude*(
          sin(x*TWO_PI/period)
        + sin(x*TWO_PI/period * 2.)
        + cos((x+PI)*TWO_PI/period * 5.)
    )/3.;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float angle = atan(uv.y,uv.x);
    float speed= 3.;

    float r = .2 + .1*sin(angle+speed*iTime)*periodicNoise(angle,2.*PI,0.9);

    float l = length(uv);
    float col = 1.-smoothstep(r-.01,r,l);
    fragColor = vec4(0., col, 0.0, 1.0);
}
