// Function to generate random numbers
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

// Function to draw petals
float drawPetal(vec2 uv, vec2 pos, float size) {
    vec2 d = uv - pos;
    float dist = length(d);
    float angle = atan(d.y, d.x);
    float petalShape = cos(angle * 6.0) * 0.5 + 0.5; // 6 petals for the shape
    return smoothstep(size, size * 0.8, dist) * petalShape;
}

// Function to create the warp effect
vec2 warpEffect(vec2 uv, float warpStrength, vec2 warpCenter) {
    vec2 d = uv - warpCenter;
    float dist = length(d);
    return uv + d * warpStrength * exp(-dist * dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Parameters
    float warpStrength = 0.6; // Strength of the warp effect
    vec2 warpCenter = vec2(0.0); // Center of the warp effect
    float petalCount = 50.0; // Number of petals
    float petalSize = 0.05; // Base size of the petals
    vec3 petalColor = vec3(1.0, 0.75, 0.8); // Color of the petals
    vec3 backgroundColor = vec3(0.0); // Background color

    // Normalized coordinates
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Apply the warp effect
    uv = warpEffect(uv, warpStrength, warpCenter);

    // Petal particles
    vec3 color = backgroundColor;
    for (int i = 0; i < int(petalCount); i++) {
        float t = iTime + float(i) * 1234.5678;
        vec2 petalPos = vec2(sin(t * 0.1), cos(t * 0.1)) * random(vec2(i, 0.0));
        petalPos += vec2(sin(t), cos(t)) * 0.5 * (1.0 + random(vec2(i, 1.0)));
        float size = petalSize + 0.02 * random(vec2(i, 2.0));
        float petal = drawPetal(uv, petalPos, size);
        color = mix(color, petalColor, petal);
    }

    fragColor = vec4(color, 1.0);
}
