float TAU = 2.*3.14159;
float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   vec3 col = vec3(0.);   
   float tt = fract(.2*iTime);

   uv *= 10.;

   float x = uv.x;
   float y = uv.y;
   for(int i = 0; i < 3; i++){
     x = x + .7*(sin(TAU*tt + .95*x - 1.05*y) + 1.8*cos(TAU*tt + x + y));
     y = y + 1.7*(sin(TAU*tt + .95*x - 1.05*y) + 1.8*cos(TAU*tt + x - y + PI));
   }

   vec2 v = vec2(x,y);   
   float p = .8*length(v) - 4.0;

   col += -p * .35;

   // Output to screen
   fragColor = vec4(col,1.0);
}
