void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float period    = 2.;
    float timescale = 1.4;
    float amplitude = 0.4;

    uv.x += sin(uv.y * period + iTime * timescale) * amplitude;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}
