#define rot(a)  mat2(cos(a+vec4(0,11,33,0)))
#define pi 3.14159265
#define t iTime

float max4d(vec4 v) { return max(max(max(v.x, v.y), v.z),v.w); }

float de(vec4 p) {
    //4d box
    p.xw *=rot(t);
    p.xy *= rot(t/2.);
    p.zw *= rot(t/3.);
	vec4 d = abs(p) - 1.;
	return length(max(d, vec4(0))) + max4d( min(d, vec4(.1)));
}

vec4 setColor(vec4 p) { 
    float theta = atan( length(p.zw), p.z);  
    vec3 cc = vec3( theta, 1.-theta, theta);
    return vec4( 1.-exp(-cc*cc ), 1);
}

void mainImage( out vec4 O, in vec2 U )
{
    U = (2.*U-iResolution.xy) / iResolution.y;
    O = vec4(0);
    vec4 ro=vec4(0,0,0,-4.5), rd = normalize( vec4(U,0,2) ), p=ro;
    float dist, i=0.; 
    for (;i<20.;i++) {
        dist = de( p=( ro + i*rd) );
        O += setColor(p) * exp(-dist*dist*3.) *exp(-i*i/20.)  ;        
    }

    O *= 3.3;
    O *= O;
}
