void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 z = 1.*fragCoord / iResolution.y;
    float scale = 1.;
    for (int i = 0; i < 8; i++) {
        vec2 w = z;
        if (z.x >= 0.5 && z.y <= 0.5) {w*=2.; w.x-=1.; }
        if (z.y >= 0.5 && z.x <= 0.5) {w*=2.; w.y-=1.; }
        if (z.x <= 0.5 && z.y <= 0.5) w*=2.; 
        if (z.x <=0.5 || z.y <=0.5) scale*=2.;
        z = w;
    }
    float d = min(min(z.x, z.y), abs(1.-z.x-z.y))/scale;
    vec3 col = vec3(1.-smoothstep(0., 0.005, d));
    fragColor = vec4(col, 1.);
}
