float border(vec2 uv) {
    return step(0., -max(abs(uv.x - 0.5), abs(uv.y - 0.5)) + 0.5);
}

void mainImage( out vec4 O, in vec2 I )
{
    float iM_Use = (iM.z <= 0.) ? 1. : 0.;

    float center = mix(0., .5, iM_Use);

    vec2 iMoff = mix(iM.xy/R.xy, vec2(0.), iM_Use)*2.;

    vec2 uv = ((I/R.xy - center) * zoom + center) - iMoff - vec2(0., 0.75);

    float bounce = tan(exp(-abs(sin(iTime*speed))))*top+(bottom-(2.-bottom));

    uv.y += bounce + 0.25;

    uv.y *= exp((bounce*stretching)); // stretching

    O = mix(vec4(vec3(-1.35+bounce),1.), texture(iChannel0, uv), border(uv));
}
