#define T 2.*iTime
#define L(r) length(p)-r


float body(vec2 p){    
    p.y -= max(p.y, 0.)*.5;
    return L(.22);
}

float head(vec2 p){    
    p.y -= .5;
    p.y *= 1.3;
    return L(.16);
}

float ears(vec2 p){
    p.x = abs(1.3*p.x) - .1;
    p.y -= .24 + max(p.y, 0.)*.6;
    return L(.04);
}

float tail(vec2 p){
    float s = sin(T + 10.*p.y), c = cos(T - .4);
    p -=  vec2((s + c)/20.,clamp(p.y ,-.6,-.2));
    return L(.045);
}

// from https://iquilezles.org/articles/distfunctions/
float smin(float a, float b){
    float k = .05;
    float h = clamp( 0.5 + 0.5*(a-b)/k, 0.0, 1.0 );
    return mix( a, b, h ) - k*h*(1.0-h);
}

void mainImage( out vec4 O, in vec2 u )
{ 
    vec3 R = iResolution;
    vec2 uv = (u+u -R.xy)/R.y;
    float aa = 2./R.y;

    O = uv.y > 0. ? vec4(.53,.81,.92,1) : vec4(1);

    uv.y -= .13;

    float d = smin(smin(smin( 
        body(uv), 
        head(uv)), 
        ears(uv)),
        tail(uv)
    );

    O = mix(O, vec4(0), smoothstep(aa, -aa, d));
}
