float sdFlower( in vec2 p, in float n, in float s)
{
    return -length(p)+s*sin(n*atan(p.y/p.x));
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5; // Centering
    uv.x*=iResolution.x/iResolution.y; // Projecting

    uv*=rotate2d(radians((5.0*sin(1.2*iTime)+1.0)*50.0));
    vec3 col = vec3(step(0.0,sdFlower(uv, sin(1.2*iTime)*16.0, 0.5)));
    col*=0.5+vec3(0.5*sin(1.2*iTime+uv.xyx+vec3(1,2,4)));
    fragColor=vec4(col,1.0);
}
