#define r mat2(cos(iTime/2. + vec4(0, 11, 33, 0)))

void mainImage(out vec4 o, vec2 u) {
    vec3 R = iResolution, q;

    float d, s, i, j,
          e = 5e-3;

    o *= 0.; 
    while(i++ < 44.) {
        q = d * (vec3(u, R.y) - R/2.) / R.y,  // p + d*D
        q.z -=2.,
        q.xz *= r;
        q.xy *= r;        

        s = length(abs(abs(q) - .3) - .3) - .3;  // s = map(q)

        for(j = 1.; j < 8.; j += j) 
            q *= j * 2., 
            s += .018 * abs(dot(sin(q), R/R)) / j;

        s = abs(s),

        s < e/3.
            ? o += (1. - s/e) / d * i / 250.  
            : o += vec4(0,0,.001,0);

        d += max(s, e / 5.);
    }
}
