#define PI 3.141592

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 rgbblend(vec3 c1, vec3 c2, float k)
{
    return mix(c1, c2, k);
}

vec3 hsvblend(vec3 c1, vec3 c2, float k)
{
    vec3 c1_hsl = rgb2hsv(c1);
    vec3 c2_hsl = rgb2hsv(c2);

    //k = 0.5-(0.5*cos(PI*k));

    vec3 mix_hsl = mix(c1_hsl, c2_hsl, k);

    float s_multi = abs((k*2.0)-1.0);
    float multi_strength = 0.7;
    s_multi = 1.0-(multi_strength*cos((3.141592/2.0)*s_multi));

    mix_hsl.y = mix_hsl.y * s_multi;

    return hsv2rgb(mix_hsl);
}

vec3 rgblinearblend(vec3 c1, vec3 c2, float k)
{
    float c1_x_lin = pow(c1.x, 2.2);
    float c1_y_lin = pow(c1.y, 2.2);
    float c1_z_lin = pow(c1.z, 2.2);


    float c2_x_lin = pow(c2.x, 2.2);
    float c2_y_lin = pow(c2.y, 2.2);
    float c2_z_lin = pow(c2.z, 2.2);

    float c_x_lin = pow(mix(c1_x_lin, c2_x_lin, k), 1.0/2.2);
    float c_y_lin = pow(mix(c1_y_lin, c2_y_lin, k), 1.0/2.2);
    float c_z_lin = pow(mix(c1_z_lin, c2_z_lin, k), 1.0/2.2);

    return vec3(c_x_lin, c_y_lin, c_z_lin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col1 = vec3(1.0, 1.0, 0.0);
    vec3 col2 = vec3(0.0, 0.0, 1.0);

    vec3 col = rgbblend(col1, col2, uv.y);
    if (uv.x > 0.4)
    {
    col = rgblinearblend(col1, col2, uv.y);
    }
    if (uv.x > 0.8)
    {
    col = hsvblend(col1, col2, uv.y);
    }

	fragColor = vec4(col,1.0);
}
