// utils from https://www.shadertoy.com/view/llySRh

vec4 char(vec2 p, int c) {
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
}
#define hue(v)  ( .6 + .6 * cos( 6.283*(v)  + vec4(0,23,21,0)  ) )


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 9.*u/R.y;
    O *= 0.;
    if ( U.x > 9. ) return;

    int n = int( T(vec2(int(U) + 9*int(U.y),0)) ); // read permutation

    U = fract(U);                                  // display digits
    O =   char(U+vec2(.2,0), 48+n/10).x * hue(float(n/10)/10.)
        + char(U-vec2(.2,0), 48+n%10).x * hue(float(n%10)/10.)*.5;

}

// see also https://www.shadertoy.com/view/lXlSzf
//          https://www.shadertoy.com/view/4s2yRD
// ref: https://en.wikipedia.org/wiki/Permutation_polynomial#Higher_degree_polynomials
//      https://www.desmos.com/calculator/ykmoljie7i

int shuffle0(int i)  // --- shuffle integers in 0..82 range (83 is prime)
{   i++;
    int a = int( mod(iTime*1e3,83.) ) + 1, 
        k = 0, b = 0; // should be: a, b such that no v with vÂ² = -b/a
    return ( a*i*i*i + b*i + k ) % 83; // permutation polynomial in prime range
}
//#define shuffle0(i) ( int( mod(iTime*1e3,83.) +1. )*++i*i*i ) % 83 // compact version

int shuffle(int i) {    // --- shuffle integers in 0..80 range
    i = shuffle0(i);
    if ( i < 81 ) return i; // compress the extra range
    i = shuffle0(i);
    if ( i < 81 ) return i;
    i = shuffle0(i);        // worst case: 2 indirections
}

void mainImage( out vec4 O, vec2 u )
{
    if ( u.y > 1. || u.x > 81. ) return;

    if ( iFrame < 1 || iMouse.w > 0. ) { // generate
        O.x = float( shuffle(int(u)) ); 
        return; 
     }

    O = T(u);                            // keep state
}
