// Fork of "mountain horizon" by MonterMan. https://shadertoy.com/view/Xfj3WR
// 2024-01-05 00:05:34

#define SAMPLE_COUNT 2

#define T_MAX 20.0
#define SHADOW_T_MAX 5.0

#define TWEAK1 (iMouse.x/iResolution.x)

float radicalInverse(int x, float base)
{
    int baseI = int(base);
    float result = 0.0;
    float b = 1.0 / base;
    while (x > 0)
    {
        result += float(x % baseI) * b;
        x /= baseI;
        b /= base;
    }
    return result;
}

vec2 hammersley(int index, int N)
{
    return vec2(float(index)/float(N), radicalInverse(index, 2.0));
}

float hash12(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 hash22b(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return 2.0 * fract((p3.xx+p3.yz)*p3.zy) - 1.0;
}

mat2 rot2d(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        c, -s,
        s, c);
}

vec3 noised(vec2 p)
{
    vec2 ip = floor(p);
    vec2 fp = fract(p);

#if 0
    float a = hash12(ip + vec2(0, 0));
    float b = hash12(ip + vec2(1, 0));
    float c = hash12(ip + vec2(0, 1));
    float d = hash12(ip + vec2(1, 1));
#else
    ip+=9.0;
    float a = texelFetch(iChannel0, (ivec2(ip)+ivec2(0,0))&255, 0).r;
    float b = texelFetch(iChannel0, (ivec2(ip)+ivec2(1,0))&255, 0).r;
    float c = texelFetch(iChannel0, (ivec2(ip)+ivec2(0,1))&255, 0).r;
    float d = texelFetch(iChannel0, (ivec2(ip)+ivec2(1,1))&255, 0).r;
#endif

    vec2 t = smoothstep(0.0, 1.0, fp);

    float result = a + (-a + b)*t.x + (-a + c)*t.y + (a - b - c + d)*t.x*t.y;
    vec2 deriv = vec2((-a + b) + (a - b - c + d)*t.y,
                      (-a + c) + (a - b - c + d)*t.x) * (6.0 * fp * (1.0 - fp));
    return vec3(result, deriv);
}

float fbm(vec2 p)
{
    float res = 0.0;
    float amp = 0.5;
    float freq = 2.0;
    vec2 du = vec2(0.0);
    for (int i = 0; i < 10; ++i)
    {
        vec3 val = noised(freq*p);
        du += val.yz;
        // erosion hack (iq): https://iquilezles.org/articles/function2009/function2009.pdf
        val.x /= (1.0 + 0.9*dot(du, du));
        res += amp * val.x;
        amp /= 2.0;
        freq *= 2.0;
    }
    return res;
}

float fbmGuard(vec2 p)
{
    float res = 0.0;
    float amp = 0.5;
    float freq = 2.0;
    vec2 du = vec2(0.0);
    for (int i = 0; i < 2; ++i)
    {
        vec3 val = noised(freq*p);
        du += val.yz;
        // erosion hack (iq): https://iquilezles.org/articles/function2009/function2009.pdf
        val.x /= (1.0 + 0.9*dot(du, du));
        res += amp * val.x;
        amp /= 2.0;
        freq *= 2.0;
    }
    return res+0.25;
}

float map(vec3 p)
{     
    float h = fbm(p.xz);
    float ground = p.y - 0.7*h;
    return ground;
}

vec3 calc_normal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001;      // replace by an appropriate value
    #define ZERO (min(iFrame,0)) // non-constant zero
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+e*h);
    }
    return normalize(n);
}

float calc_shadow(vec3 p, vec3 l, float k)
{
    float res = 1.0;

    float t = 0.0;
    for (int i = 0; i < 256 && t < SHADOW_T_MAX; ++i)
    {
        float dist = map(p + t * l);
        if (dist < 0.0001)
        {
            return 0.0;
        }
        res = min(res, k*dist/t);
        t += dist;
    }

    return res;
}

vec3 render(vec2 uv)
{
    vec3 ro = vec3(0.1, 0.7, -1);
    ro.z += 0.1*iTime;
    vec3 at = vec3(0, 0.3, ro.z+1.0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.3 * cam_z);

    float t = 0.0;
    bool hit = false;
    for (int i = 0; i < 256 && t < T_MAX; ++i)
    {
        vec3 p = ro + t * rd;

        float dist = p.y - 0.7*fbmGuard(p.xz);
        if (dist < 0.1)
        {
            dist = map(p);
        }
        if (abs(dist) < 0.0001)
        {
            hit = true;
            break;
        }
        t += 0.25*dist;
    }

    vec3 sky_col = 0.7*vec3(0.45, 0.6, 0.8);
    vec3 sun_col = 1.0*vec3(0.93, 0.8, 0.7);
    vec3 sun_dir = normalize(vec3(0.4, 0.3, 0.9));

    float sunAmount = max(0.0, dot(rd, sun_dir));

    vec3 col = vec3(0);
    if (hit)
    {
        vec3 p = ro + t * rd;
        vec3 n = calc_normal(p);
        vec3 shadow = pow(vec3(calc_shadow(p+0.003*n, sun_dir, 8.0)), vec3(1.0, 1.4, 1.7));

        vec3 dirtCol = vec3(0.37, 0.3, 0.23);
        vec3 snowCol = vec3(0.6, 0.6, 0.6);
        vec3 albedo = mix(dirtCol, snowCol, smoothstep(0.5, 0.6, n.y));
        col += max(0.0, dot(n, sun_dir)) * sun_col * albedo * shadow;
        col += 0.2*max(0.0, n.y * sun_dir.y) * sun_col * albedo;
        col += 0.2*max(0.0, dot(n, vec3(0,1,0))) * albedo * sky_col;

        vec3 fog_col = mix(sky_col, sun_col, pow(sunAmount, 8.0));

        // https://iquilezles.org/articles/fog/
        float a = 0.15;
        float b = 0.3;
        float fogAmount = (a/b) * exp(-ro.y*b) * (1.0 - exp(-t*rd.y*b))/rd.y;
        col = mix(col, fog_col, clamp(fogAmount, 0.0, 1.0));

        //col = vec3(ao);
    }
    else
    {
        col = mix(sky_col, sun_col, pow(sunAmount, 16.0));
    }

    // Output to screen
    col = sqrt(col);
    col = mix(col, smoothstep(0.0, 1.0, col), 0.5);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0 * uv - 1.0;
    float ar = iResolution.x/iResolution.y;
    uv.x *= ar;
    vec2 pixelDim = 2.0*vec2(ar, 1.0) / iResolution.xy;

    vec3 col = vec3(0);
    int sampleN = SAMPLE_COUNT;
    for (int i = 0; i < sampleN; ++i)
    {
        col += render(uv + pixelDim*hammersley(i, sampleN));
    }
    col /= float(sampleN);

    fragColor = vec4(col,1.0);
}
