void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Normalized pixel coordinates (from 0 to 1)
   vec2 uv = fragCoord/iResolution.xy;

   // Normalized mouse coordinates (from 0 to 1)
   vec2 mouse = iMouse.xy/iResolution.xy;

   // Create a sphere
   float sphere = length(uv - mouse) - 0.1;

   // Add spikes to the sphere
   float spikes = sin(uv.x*360.0)*0.05;

   // Combine the sphere and spikes
   float finalShape = sphere + spikes;

   // Create the color based on the final shape
   vec3 color = vec3(finalShape);

   // Output to screen
   fragColor = vec4(color,1.0);
}
