void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;

    vec3 col = sin(0.5 + 0.5)*cos(fract(iTime+uv.xyx+vec3(0,2,4)));

    col *= fract(5.2 / 0.5 - fract(col));
    col /= iTime*fract(col + cos(iTime+col));
    col /= iTime-fract(col + fract(cos(col) + sin(iTime-col) / iTime+cos(iResolution.y * -iResolution.x)));
    col = fract(col + fract(cos(iResolution.x) / sin(col)));
    col - col / fract(col);
    col -= fract(step(0.2+0.5, fract(col) / fract(cos(col))));
    col /= fract(sin(-col / col) * fract(iTime+cos(iResolution.y/cos(iResolution.x/col)/iTime)));

    fragColor = vec4(col,1.0);
}
