vec3 palette( float t ) {
    vec3 a = vec3(0.038, 0.488, 0.093);
    vec3 b = vec3(0.725, 0.913, 0.284);
    vec3 c = vec3(0.218, 1.443, 1.182);
    vec3 d = vec3(0.304, 3.387, 2.572);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 1.1; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));;

        vec3 col = palette(length(uv0) + i*.2 + iTime*.22);

        d = sin(d*17. + iTime)/7.2;
        d = abs(d);

        d = pow(0.02 / d, 2.0);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);

}
