// Fork of "crack recursion" by asis5528. 
// 2024-08-06 06:24:05

bool far = false;//effect strength according to distance from origin
float samples = 9.0;//quality of effect
float radius = 1.2;//radius of mask
float speed = 0.15;//speed of recursion
vec2 maskOrigin = vec2(0.0,0.0);//position of circular mask
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 nt = uv;
    vec2 tt = (2.*fragCoord-iResolution.xy)/iResolution.y;
   // vec2 mt = (2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float dis1 = length(tt-maskOrigin);
   // float dis = (1.-smoothstep(radius,radius-0.6,length(tt-maskOrigin)))*(far?(dis1*0.65):1.0);
    float dis = (far?(dis1*0.65):1.0);
    float dis2 = smoothstep(radius,radius-0.35,length(tt-maskOrigin));
    vec3 col = vec3(0.);
vec3 org = texture(iChannel0,uv).rgb;
    vec3 col2 = vec3(0.);

    float gTime = iTime*speed;

    vec2 st = uv;
    float stp = samples;


for(float i = 0.0;i<stp;i+=1.0){
        uv = st;
        uv-=0.5;
        float stlen = (i*2.0)/(stp/2.0);
        float llne = (1.*2.0)/(stp/2.0);
        float time = (fract(gTime+stlen+llne));
        uv*=1.-time*0.3*dis;
        uv+=0.5;
        time = time*2.0;
        time = abs(time-1.0);
        col =mix(col,texture(iChannel0,uv).rgb,(1.-time));
}

for(float i = 0.0;i<stp;i+=1.0){
        uv = st;
        uv-=0.5;
        float stlen = (i*2.0)/(stp/2.0);
        float llne = (10.*2.0)/(stp/2.0);
        float time = (fract(gTime+stlen+llne));
        uv*=1.-time*0.3*dis;
        uv+=0.5;
        time = time*2.0;
        time = abs(time-1.0);   
        col2 =mix(col2,texture(iChannel0,uv).rgb,(1.-time));
}




col = (col+col2)/2.0;

col = mix(col,org,dis2);
    fragColor = vec4(col,1.0);
    //fragColor = vec4(dis2);
}
