float random (in vec2 st) {
    return sin(sin(iTime + dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float speed = 0.2;
    float time = iTime * speed;

    vec2 uv = fragCoord.xy / iResolution.xy;
    float shakingForce = 0.05;
    uv += vec2(sin(uv.y + time), cos(uv.x + time)) * shakingForce;

    float sizeWave = 5.;
    float noise = sin(uv.x * sizeWave + time) * sin(uv.y * sizeWave + time) * 0.01;

    vec3 finalColor = vec3(1);
    finalColor *= texture(iChannel0, uv).xyz * 1.2;
    float shiftForCam = 0.01;
    uv.x += shiftForCam;
    finalColor *= texture(iChannel0, uv).xyz;


    vec3 narcColor = 0.9 + 0.1 * cos(iTime + uv.xyx + vec3(0,2,4));
    finalColor *= narcColor * 1.1;


    fragColor = vec4(finalColor, 1.0);
}
