void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    float focalPoint = iMouse.z > 0.0 ? iMouse.y/iResolution.y : sin(iTime) * 0.5 + 0.5;

    float blur = pow(uv.y - focalPoint, 2.0);
    blur *= 32.0;

    float dither = texture(iChannel1, fragCoord / iChannelResolution[1].xy).r * 64.0;

    vec2 offset = normalize(vec2(mod(dither, 4.0), mod(floor(dither / 4.0), 4.0)) / 2.0 - 1.0) * floor(dither / 16.0);

    vec3 col = texture(iChannel0, uv + offset * blur / iResolution.xy).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
