#define PI 3.1415926535897932
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(texture(iChannel0,(fragCoord-vec2(5.,0.))/iResolution.xy).r,texture(iChannel0,fragCoord/iResolution.xy).g,texture(iChannel0,(fragCoord+vec2(5.,0.))/iResolution.xy).b,1.);
}

#define PI 3.1415926535897932
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y * 2.;
    vec2 uvS = vec2(uv.x+uv.y,uv.x-uv.y);
    uvS = .5*uvS+.5*vec2(sin(2.*PI*(uvS.x+.5))/(2.*PI)+uvS.x,sin(2.*PI*(uvS.y+.5))/(2.*PI)+uvS.y);
    uv = vec2(uvS.x+uvS.y,uvS.x-uvS.y)*.5;
    vec3 col = vec3((sin(uv.y)+1.)*.5,(sin(uv.y+2.*PI/3.)+1.)*.5,(sin(uv.y+4.*PI/3.)+1.)*.5);
    if(mod(uv.x,.1)<.01||mod(uv.y,.1)<.01||mod(uv.x,.1)>.09||mod(uv.y,.1)>.09){
        col=vec3(1.)-col;
    }
    fragColor = vec4(col,1.);
}
