// Fork of "Galaxy Fractal" by wyatt. https://shadertoy.com/view/XcSyzV
// 2024-08-02 13:17:37

Main {
    Q = A(U)/float(iFrame);
    Q = 1.-atan(Q);
}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
#define phi 1.618

Main {
    Q = A(U);
    vec2 V = 2.*(U-.5*R)/R.y;
    V.y -= .05;
    V *= ei(2.*pi/4.);
    for (float i = 0.; i < 30.; i++) {
        vec3 W = vec3(V,0);
        float c = 1.;
        for (float j = 1.; j < 8.; j++) {
            vec4 h = hash44(vec4(i,j,iTime,iFrame));
            float N = 5.;
            float n = floor(h.z*N)/N;
            float a = 2.*pi*n;
            W *= 2.;
            W.xy *= ei(a);
            W.x +=1.;
            c += n;

            vec4 dQ= exp2(j)*exp(-180.*exp2(-j)*abs(length(W.xy)-.75))*max(sin(-2.+.5*j+vec4(1,2,3,100)),0.);
            Q.xyz += .3*dQ.xyz;
        }
    }



}
