float squareWave(float x, float period) {
    return sign(sin(2.0 * 3.141592653589793238 * x / period));
}

float fourierSeries(float x, int n) {
    float result = 0.0;
    for (int i = 1; i <= n; i += 2) {
        result += (1.0 / float(i)) * sin(2.0 * 3.141592653589793238 * float(i) * x);
    }
    return 0.5 * (1.0 + result);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float x = fragCoord.x / 100.0;
    float y = fourierSeries(x, 10);
    fragColor = vec4(y, y, y, 1.0);
}
