void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized coords
    vec2 uv = fragCoord/iResolution.xy;

    // middle line
    vec3 col = vec3(0.3);
    col+=mix(vec3(0), vec3(.3,.3,.4), float(abs(uv.y-.4925)<.0025));

    // grid lines
    float numLines = 11.;
    col+=mix(vec3(0), vec3(.2), float(abs(mod(uv.y,1./numLines)-.038)<.002));

    // sine wave
    float speed = mix(iMouse.x/iResolution.x, .4, step(iMouse.x, .5));
    float frequency = 14.;
    float amplitude = mix(iMouse.y/iResolution.y, .6, step(iMouse.y, .5));
    float wave = float(abs(uv.y-(sin((iTime*speed+uv.x)*frequency)*.5*amplitude)-.5)<.005);
    col += mix(vec3(0), vec3(.6,.3,.6), wave);

    // output
    fragColor = vec4(col,1.0);
}
