void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float t = iTime * 1.75;
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    uv *= 10.;

    vec2 gridUv = fract(uv);
    vec2 gridId = floor(uv);

    float area = step(atan(gridUv.x, gridUv.y), 0.785);//45degrees to radians
    float area2 = (1.0 - area);

    vec3 res = mix(area * vec3(1.0, 0.2, 0.33), 
                   vec3(area2), 
                   sin(t + gridId.x + gridId.y) * 0.5 + 0.5);

    fragColor = vec4(res, 1.0);
}
