float sdStar(in vec2 p, in float r, in int n, in int d) {
    n = max(n, 2);

    const float pi = 3.141592653589793;
    float an = pi/float(n);

    float m = float(n)/2.0 - clamp(float(d), floor(float(4-n)/2.0), float(n)/2.0) + 1.0;

    vec2 acs = vec2(cos(an),sin(an));
    vec2 ecs = vec2(cos(an*m),sin(an*m));

    p.x = abs(p.x);

    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));

    p -= r*acs;
    p += ecs*clamp(-dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float map(vec2 p) {
    float n = (sin(iTime * 0.3) + 2.0) * 5.0;
    float d = n/2.5;

    return sdStar(p, 0.75, int(n), int(d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // iq's shading https://iquilezles.org/
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float d = map(p);
    vec3 col = (d<0.0) ? vec3(0.6,0.8,1.0) : vec3(0.9,0.6,0.3);
    col *= 1.0 - exp(-9.0*abs(d));
	col *= 1.0 + 0.2*cos(128.0*abs(d));
	col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if (iMouse.z > 0.001) {
        d = map(m);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}
