void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy-0.5;     
    uv.x*=iResolution.x/iResolution.y;
    float B= 0.;

    for(float x=-0.1;x<=0.1;x=x+0.0004)
    {
        for(float y=-0.01;y<=0.01;y=y+0.0004)
        {
            B+=pow((uv.y-y)*(uv.y-y),0.5)/pow(pow(uv.x-x,2.)+pow(uv.y-y,2.),1.5);
        }
    }
    vec3 col =vec3(0.);
    B=B/10000.;

    for(float i=0.;i<=100.;i++)
    {
        if(B > 10.*i && B < 10.*i+1.){
            col=vec3(1.);            
            break;
        }
    }

    if((step(uv.x,0.1)*step(-0.1,uv.x)*step(uv.y,0.01)*step(-0.01,uv.y))==1.)
    {
        col=vec3(1.);
    }

    fragColor = vec4(col,1.0);
}
