mat2 rot2D(float r){
    return mat2( cos(r),sin(r),-sin(r),cos(r) );
}

void mainImage( out vec4 o, in vec2 FC ) {

vec2 r = iResolution.xy;
float t = iTime;
o = vec4(0);

for(float i,e,g;i++<5e1;){
    vec3 p = vec3( (FC.xy-0.5*r)/r.y*g, g-4.0 );
    p.xy *= rot2D(t);
    p.xz *= rot2D(t);
    e = length(p+0.2)-1.0;
    e = min(e,length(p-clamp(p,-0.5,1.0)));
    g+= e*0.96;
    o+= 0.01/exp(max(0.0,e)*4e5);
}

}
