void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x /= iResolution.y / iResolution.x;

    vec3 A = vec3(sin(iTime) * 2., 0., cos(iTime) * 2. + 2.);

    vec3 P = vec3(0, cos(iTime / 5.) / 4., -2.);
    vec3 V = vec3(uv, 2);
    V /= length(V);

    vec3 col = vec3(0);
    float LL;

    for (float L = 0.; L < 20.; L += 0.01) {
        P += V * 0.01;
        LL = length(P - vec3(0, 0, 2) );
        V -= (P - vec3(0, 0, 2)) / (LL * LL) / 500.;

        if (P.z > 6.) {
            col = vec3(max(sin(P.x * 20.), sin(P.y * 20.))) * 0.25;
            break;
        }

        if (length(P - vec3(0, 0, 2)) < 0.3) {
            col = vec3(1, 0.75, 0);
            break;
        }

        if (length(P - vec3(0, 0, 2)) < 0.6 && abs(P.y) < 0.01) {
            col = vec3(0.75, 0.5, 0);
            break;
        }

        if (length(P - A) < 0.05) {
            col = vec3(0.25, 1, 0.5);
            break;
        }
    }

    fragColor = vec4(col, 1.0);
}
