void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 delta = fragCoord.xy - iMouse.xy;

    float t = min(1.0, iTime / 1.8);
    t = min(1.0, t);

    float len = length(delta)*0.08;
    float wave_progress = len - t*26.0;

    float wave = (1.0 + sin(wave_progress));
    wave *= 2.0*exp(-length(delta)*0.007)*min(1.0, t);
    wave *= smoothstep(0.0, 1.0, 1.0 - 2.0 * abs(0.5 - t));

    uv -= normalize(delta)*0.008*wave;

    fragColor = texture(iChannel0, uv);
    fragColor.xyz += wave*0.3;
}
