void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 buffer0 = texelFetch(iChannel0, ivec2(fragCoord), 0);
    vec4 buffer1 = texelFetch(iChannel1, ivec2(fragCoord), 0);
    vec4 buffer2 = texelFetch(iChannel2, ivec2(fragCoord), 0);

    fragColor = vec4(buffer0.x, buffer1.x, buffer2.x, 0.0);
}

uint hash(inout uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

float rand(inout uint seed)
{
    return fract(float(hash(seed))/ 4294967296.0);//4294967296 2 ^ 32 / 32 bit unsigned integer limit
}

vec2 loop(in vec2 vec, in vec2 bounds)
 {
     vec = vec2(mod(vec.x, bounds.x), mod(vec.y, bounds.y));
     return vec;
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixelSeed = fragCoord.x * iResolution.y + fragCoord.y;
    uint seed = uint(pixelSeed * mod(float(iFrame) + iDate.w, 1000.0));

    if(iFrame == 1)
    {
        float random = rand(seed);

        float color = float(random > 0.5);

        fragColor = vec4(color, 0.0, 0.0, 0.0);
        return;
    }

    vec4 buffer0 = texelFetch(iChannel0, ivec2(fragCoord), 0);

    bool alive = buffer0.x == 1.0;

    fragColor = vec4(0.0);

    int total = -int(alive);

    for(int y = -1; y < 2; y++)
    {
        for(int x = -1; x < 2; x++)
        {
            bool silly = texelFetch(iChannel0, ivec2(loop(fragCoord + vec2(x,y), iResolution.xy)), 0).x == 1.0;
            total += silly ? 1 : 0;
        }
    }

    if(total > 3)
     {
         fragColor = vec4(0.0);
         return;
     }
    if(total + int(alive) > 2)
    {
        fragColor = vec4(1.0,0.0,0.0,0.0);
    }
}
