void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ycoord = uv.y;
    uv.y -= iTime/1.5f;
    vec2 coord = vec2(uv);

    float intensity1 = texture(iChannel1,coord).r;
    float intensity2 = texture(iChannel0,coord).r;

    float intensity = (intensity1*2.0+intensity2*0.5)/2.0;

    vec3 fire_color = vec3(
		1.5 * intensity,
		1.5 * pow(intensity, 3.0),
		pow(intensity, 6.0)
	);

    float alpha = 0.5/ycoord * (1.0-pow(ycoord,5.0));
    // Output to screen

    fire_color *= alpha;
    fragColor = vec4(fire_color,0.0);
}
