const vec3 COLOR = vec3(.5,.75,1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Map screen pixels to 1920x1080 range
    vec2 c = fragCoord/iResolution.xy * vec2(1920, 1080);

    // Metaball brightness computations
    float brightness = 0.0;
    for (int i=0;i<20;i++) {
        float t = iTime - 7.5 * float(i);
        float metaball_x = 960.+sin(t/2.5 * (float(i&2)-.5))*(mod(t,150.))*25.;
        float metaball_y = 540.+cos(t/2.5 * (float(i&2)-.5))*(mod(t,150.))*25.;
        float metaball_r = mod(t,150.) * .35 * (150.-mod(t,150.))/150.;
        brightness += metaball_r/(distance(vec2(metaball_x,metaball_y),c));
    }

    // Apply filters
    brightness = .1/(-(brightness)-.1) + 1.; // map  to 0-1 range
    brightness = brightness<.4? brightness/2. :
                 brightness<.6 ? brightness*3. - 1. :
                 (brightness+1.)/2.; // Brightness curve
    brightness *= brightness; // gamma 2

    // Map brightness to color
    vec3 col = brightness<.5 ? 2.*brightness*COLOR :
                               COLOR + (2.*brightness-1.)*vec3(1.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
