void mainImage(out vec4 c, vec2 v) {
    v /= iResolution.x;
    v = v * 8.0 - vec2(4, 2);
    c.a = 1.0;
    c.b = float(dot(v, v) > 1.0 && v.y < 0.0) * fract(length(v) - 0.5);
    c *= fract(vec4((v.x / (2.0 * smoothstep(-1.0, 1.0, v.x) - 1.0)) * v.y + iTime));
    c.b += sqrt(max(0.0, 0.9 - dot(v, v))) * 0.6 * min(1.0, exp(-v.y * v.y));
    c.rg = vec2(abs(v.x) + 0.25 * v.y + 0.5);
    c.rg *= vec2(dot(v, v) > 0.9 && c.g < 0.99 && v.y > 0.0) * 0.6;
    c.r += fract(float((v.x / (2.0 * smoothstep(-1.0, 1.0, v.x) - 1.0)) * v.y + 0.5 *  iTime)) * float(v.y < 0.0 && dot(v, v) > 1.0 && dot(v, v) < 6.0 + 2.0 / v.x / v.x) / (1.0 + dot(v, v));

    c.b *= float(length(v - vec2(1.4, 1.2)) > 1.1);
    c.b *= float(length(v - vec2(-1.4, 1.2)) > 1.1);

    c.g += sin(v.y * 3.2 + 3.2 * iTime) / dot(v, v) * float(v.y > 0.0 && c.b == 0.0 && c.r == 0.0) / (dot(v, v) + 1.0) + 0.05;
    c.g = max(0.0, min(1.0, c.g));

    c += pow(1.0 / (1.0 + length(v - vec2(0.6, 0.5))), 3.0);
    c += pow(1.0 / (1.0 + length(v - vec2(-0.6, 0.5))), 3.0);
}
