void mainImage(out vec4 c, in vec2 v) {
    c = texelFetch(iChannel0, ivec2(v), 0);
}

void mainImage(out vec4 c, in vec2 v) {
    #define r(e) (94587u * e ^ e.wxyz)
    #define tx(e) texture(iChannel0, v / iResolution.xy + e)
    vec2 o = vec2(1, -1) / iResolution.x;
    vec4 t = tx(0.);
    vec4 ta = tx(o * sin(iTime)),
         tb = tx(-o * cos(iTime)), 
         tc = tx(o.xx * cos(iTime)), 
         td = tx(o.yy * sin(iTime * 3.));
    #define f(e) (0.25 * (e.x + e.y + e.z + e.w))
    c = vec4(ta.r, tb.g, tc.b, td.a);
    c = length(t) > length(c)? t - cos(iTime / 5.) * 0.1 * c 
                             : c + cos(iTime / 3.) * 0.05 * t;
    c = iFrame < 1 ? vec4(r(r(r(uvec4(v + v, v))))) / vec4(-1u) + 0.01 : c;
}
